</template>

<script lang="ts" setup>
const isDark = useDark()
const toggleDark = useToggle(isDark)
const isAnimating = ref(false)
const linkList = [
  { title: 'Home', path: '/' },
  { title: 'Experiment', path: '/experiment' },
]

const handleToggleClick = (e: MouseEvent) => {
  if (isAnimating.value) return

  isAnimating.value = true
  const toggle = e.currentTarget as HTMLElement
  toggle.classList.add('animate')

  setTimeout(() => {
    toggle.classList.toggle('active')
    toggleTheme(e)
  }, 150)

  setTimeout(() => {
    toggle.classList.remove('animate')
    isAnimating.value = false
  }, 300)
}

const toggleTheme = (event: MouseEvent) => {
  const willDark = !isDark.value

  // 检查浏览器是否支持 View Transitions API
  if (!supportsViewTransitions() || isReducedMotion()) {
    toggleDark()
    return
  }

  const x = event.clientX
  const y = event.clientY
  const endRadius = Math.hypot(
    Math.max(x, window.innerWidth - x),
    Math.max(y, window.innerHeight - y)
  )

  const transition = document.startViewTransition!(() => {
    toggleDark()
  })

  transition.ready.then(() => {
    const clipPath = [
      `circle(0px at ${x}px ${y}px)`,
      `circle(${endRadius}px at ${x}px ${y}px)`
    ]
    document.documentElement.animate(
      {
        clipPath: willDark ? clipPath : [...clipPath].reverse()
      },
      {
        duration: 500,
        easing: 'ease-in',
        pseudoElement: willDark
          ? '::view-transition-new(root)'
          : '::view-transition-old(root)'
      }
    )
  })
} 
</script>

<style scoped>
.path {
  fill: none;
  stroke: var(--foreground);
  cursor: pointer;
  stroke-dasharray: 1000;
  stroke-dashoffset: 1000;
  stroke-width: 0.5;
  animation: dash 12s linear alternate forwards;
}

@keyframes dash {
  to {
    stroke-width: 0.5;
    stroke-dashoffset: 0;
  }
}
</style>